#include <stdio.h>
#include <math.h>
#include "values.h"
#include "example.h"

int main(int argc, char** argv)
{
    printf("%s\n", MESSAGE);
    printf("The square root of 2.0 is %f\n", sqrt(2.0));
    printf("5! is %lu\n", factorial(5));
}

#include "example.h"

unsigned long factorial(unsigned long i)
{
    return i == 1 ? 1 : i * factorial(i - 1);
}

/* Math functions */
external unsigned long factorial(unsigned long i);
/* String functions */
external void reverse_string(char* str);

#include "example.h"

unsigned long factorial(unsigned long i)
{
    return i == 1 ? 1 : i * factorial(i - 1);
}

#include <string.h>
#include "example.h"

unsigned void reverse_string(char* str)
{
    char* front = NULL;
    char* back = NULL;
    char tmp = 0;

    for (front = str, back = str + strlen(str) - 1;
         back > front;
         front++, back--)
    {
        tmp = *front;
        *front = *back;
        *back = tmp;
    }
}

#include <stdio.h>
#include <math.h>
#include "values.h"
#include "example.h"

int main(int argc, char** argv)
{
    char reverse[] = MESSAGE;

    reverse_string(reverse);

    printf("%s\n", MESSAGE);
    printf("Reversed: %s\n", reverse);
    printf("The square root of 2.0 is %f\n", sqrt(2.0));
    printf("5! is %lu\n", factorial(5));
}

SUBDIRS="math string"
make() { mk_library \ LIB=example \ GROUPS="math/math.og string/string.og"
mk_program \ PROGRAM=hello \ SOURCES="main.c" \ INCLUDEDIRS="include" \ LIBDEPS="m example" }
